mapper.update:
UPDATE `order`
SET `state` = #{state},
tx_id = #{txId}, tx_host= #{txHost}, tx_user_id= #{txUserId}, tx_date= #{txDate}
WHERE order_id = #{orderId}
AND tx_status=1

UPDATE `order`
SET tx_id = #{txId}, tx_host= #{txHost}, tx_user_id= #{txUserId}, tx_date= #{txDate}
WHERE order_id = #{orderId}
AND tx_status=1

mapper.select:
SELECT address_id as addressId
FROM `order`
WHERE order_id = #{orderId}
